import './App.css';
import CircleSelectors from './component/CircleSelector/CircleSelector';
import Circles from './component/Circles/Circles';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentCircle: 1,
      circles: [1,2,3,4]
     }

this.selectCircle = this.selectCircle.bind(this);
  }

selectCircle (id) {
  console.log(`Button Clicked + ${id}`);
this.setState({currentCircle: id})
console.log(this.state.currentCircle)
} 

test (t) {
  if (t == this.state.currentCircle) {
  return true
  }  
}

  render() {
    return (
      <div className="App">
        <header className="App-header">UNIT 4 FINAL ASSESSMENT</header>
        <main>
         <CircleSelectors test={this.test} circles={this.state.circles} selectedButton={this.state.currentCircle} selectCircle={this.selectCircle} />
         <Circles circles={this.state.circles} selectedButton={this.state.selectedButton} />
        </main>
      </div>
    );
  }
}

export default App;
