import React from 'react';
import './CircleSelector.css';


const CircleSelectors = (props) => {
 let buttons = [];

 for(let i = 0; i< props.circle; i++ ) {
     if(props.currentCircle === i ) {
        buttons = <p>bye</p>
     }
     else {
         buttons = <p>hi</p>
     }
 };

return(
    <div className='CircleSelector'>
      {buttons}
      {/* {
      props.circles.map((circle, idx) =>
        <button
         key={idx}
          id={idx + 1}
        className={idx+1 === props.currentCircle ? '' : 'selected'}
          onClick={e => props.selectCircle(e.target.id)}
      >Selected Circle {circle}</button> 
      )} */}
    </div>
)
  };

export default CircleSelectors;
